class t{constructor(t){this.errorMessageId="",this.host=t}get errorMessage(){return this.host.querySelector("ids-error-message")}get inputField(){return this.input||this.textarea||this.select}get input(){return this.host.querySelector("input")}get textarea(){return this.host.querySelector("textarea")}get select(){return this.host.querySelector("select")}get label(){return this.host.querySelector("label")}get button(){return this.host.querySelector("ids-button")}get icon(){return this.host.shadowRoot?.querySelector(".ids-icon")||this.host.querySelector(".ids-icon")}get hint(){return this.host.querySelector('[slot="hint"]')}hostConnected(){this.setupHostObserver(),this.setupInputObserver()}setupInputObserver(){if(this.inputField){new MutationObserver((t=>{t.forEach((t=>{"attributes"==t.type&&"disabled"===t.attributeName&&this.inputDisabledChanged()}))})).observe(this.inputField,{attributes:!0})}}inputDisabledChanged(){}setupHostObserver(){new MutationObserver((t=>{t.forEach((t=>{"attributes"==t.type&&("invalid"===t.attributeName&&this.invalidChanged(),"light"===t.attributeName&&this.lightChanged())}))})).observe(this.host,{attributes:!0})}invalidChanged(){}lightChanged(){}showErrorMessage(){this.errorMessage&&(this.errorMessage.show=!0)}hideErrorMessage(){this.errorMessage&&(this.errorMessage.show=!1)}setInputInvalid(t){t.setAttribute("aria-invalid","true"),this.host.setAttribute("invalid","true")}setInputValid(t){t.removeAttribute("aria-invalid"),this.host.getAttribute("invalid")&&this.host.removeAttribute("invalid")}}export{t as BaseController};
